import { HttpParams } from '@angular/common/http';
import { Component } from '@angular/core';
import { MatDialog } from '@angular/material/dialog';
import { RecipeService } from 'src/app/services/recipe.service';
import { CreateRecipeComponent } from './create-recipe/create-recipe.component';


@Component({
  selector: 'app-my-recipes',
  templateUrl: './my-recipes.component.html',
  styleUrls: ['./my-recipes.component.css']
})
export class MyRecipesComponent {

  public recipes = [] as any;
  public queryParams = new HttpParams();
  public ingredients: string;

  makeNoItemsElementVisible = true;

  constructor(private _recipeService: RecipeService, private dialog:MatDialog){

  }

  ngOnInit(): void {
    this.loadRecipes()
  }

  async loadRecipes() {
    this.queryParams = this.queryParams.append("defaultRecipes", "no")
    this.recipes = await this._recipeService.getRecipes(this.queryParams);

    if(this.recipes.length > 0){
      this.makeNoItemsElementVisible = false;
    }
    else{
      this.makeNoItemsElementVisible = true;
    }
  }

  openDialog(){
    const dialogRef=this.dialog.open(CreateRecipeComponent, {
      data: this.ingredients,
      width: '900px',
    })
    dialogRef.afterClosed().subscribe(result => {
      this.ngOnInit();
    });
  }
}
<div class="container">
    <div class="top">
      <h1 >My recipes</h1>
      <button mat-raised-button class="create" (click)="openDialog()" ><mat-icon>add</mat-icon>Create recipe</button>
    </div>
    <div *ngIf="makeNoItemsElementVisible">
      <h3 class="noItemsElement">You don't have any fridge items yet.</h3>
    </div>
    <div>
      <app-recipe-list class="recipe-list" [recipes]="recipes" (restartAfterEvent)="loadRecipes()"></app-recipe-list>
    </div>
</div>
