import { HttpClient, HttpParams } from '@angular/common/http';
import { Component, EventEmitter, Input, Output } from '@angular/core';
import { Recipe } from 'src/app/models/recipe';
import { RecipeService } from 'src/app/services/recipe.service';
import { MatDialog } from '@angular/material/dialog';
import { RecipeDetailsComponent} from 'src/app/shared/components/recipe-details/recipe-details.component';
import { EditRecipeComponent } from './edit-recipe/edit-recipe.component';


@Component({
  selector: 'app-recipe-list',
  templateUrl: './recipe-list.component.html',
  styleUrls: ['./recipe-list.component.css']
})
export class RecipeListComponent {

  @Input() public recipes;

  @Output() public restartAfterDeleteEvent = new EventEmitter();

  constructor(private _recipeService:RecipeService,
    public dialog:MatDialog) {
  }

  ngOnInit(){
  }

  async deleteRecipe(id: string){
    console.log("del")
    const text = await this._recipeService.deleteRecipe(id);
    this.restartAfterDeleteEvent.emit()
  }

  openDialog(selectedRecipe: Recipe){
    this.dialog.open(RecipeDetailsComponent, {
      width: '900px',
      data: {
        selectedRecipe: selectedRecipe
      }
    });
  }

  openDialogEdit(selectedRecipe: Recipe){
    this.dialog.open(EditRecipeComponent, {
      width: '900px',
      data: {
        selectedRecipe: selectedRecipe
      }
    });
  }
}
<div class="recipe-grid">
    <mat-card *ngFor="let recipe of recipes" matRipple class="clickable" (click)="openDialog(recipe)">

        <img mat-card-image [src]="recipe.image">

        <mat-card-header>
          <mat-card-title>{{recipe.name}}</mat-card-title>
        </mat-card-header>

        <mat-card-actions>
          <button mat-icon-button (click)="openDialogEdit(recipe);  $event.stopPropagation()">
            <mat-icon>edit</mat-icon>
          </button>
          <button mat-icon-button (click)="deleteRecipe(recipe.id);  $event.stopPropagation()">
            <mat-icon>delete</mat-icon>
          </button>
        </mat-card-actions>

    </mat-card>
</div>
