import { Component, OnInit } from '@angular/core';
import { Account } from 'src/app/models/account';
import { ISecurityQuestion } from 'src/app/models/securityQuestions'
import { AccountService } from 'src/app/services/account.service';
import { Router } from '@angular/router';

@Component({
  selector: 'app-account-view',
  templateUrl: './account-view.component.html',
  styleUrls: ['./account-view.component.css']
})
export class AccountViewComponent implements OnInit {
  account: Account;
  securityQuestion: ISecurityQuestion[]; 

  constructor(private _accountService: AccountService, private router: Router){
    this.account = {
      id: "",
    name: "",
    email: "",
    password: "",
    securityQuestion: "",
    securityAnswer: ""
    }

  }

  async ngOnInit() {
    this.account = await this._accountService.getAccountInfo()
    this.securityQuestion = await this._accountService.getSecurityQuestions()
  }

  navigateToUpdateAccount(){ 
    this.router.navigate(['update_account'])
  }

  /* getSecurityQuestionText() { 
    forEach() { 

    }
  }
    
  
  if securityQuestion.enumValue == account.securityQuestion --> return securityQuestion.text */
}
<mat-card class="profile-container">
  <mat-card-header>
    <h1>Account Settings</h1>
    <button id="editButton" mat-raised-button (click)="navigateToUpdateAccount()">Edit</button>
  </mat-card-header>

  <div class="profile-info-section">

    <div class="info-block">
        <h3>Name</h3>
        <p class="detail"> {{account.name}}</p>
    </div>

    <div class="info-block">
        <h3>E-Mail</h3>
        <p class="detail"> {{account.email}}</p>
    </div>

    <div class="info-block">
        <h3>Password</h3>
        <p class="detail"> ********</p>
    </div>

    <div class="info-block">
        <h3>Security Question</h3>
        <p class="detail"> {{account.securityQuestion}}</p>
    </div>

  </div>
</mat-card>
