import { Component, OnInit } from '@angular/core';
import { Account } from 'src/app/models/account';
import { ISecurityQuestion } from 'src/app/models/securityQuestions'
import { AccountService } from 'src/app/services/account.service';
import { Router } from '@angular/router';

@Component({
  selector: 'app-account-view',
  templateUrl: './account-view.component.html',
  styleUrls: ['./account-view.component.css']
})
export class AccountViewComponent implements OnInit {
  account: Account;
  securityQuestion: ISecurityQuestion[];

  isDisabled: boolean = true;
  btnText: string = "Edit";
  fakePassword: string = "*********"

  constructor(private _accountService: AccountService, private router: Router){
    this.account = {
      id: "",
    name: "",
    email: "",
    password: "",
    securityQuestion: "",
    securityAnswer: ""
    }

  }

  async ngOnInit() {
    this.account = await this._accountService.getAccountInfo()
    this.securityQuestion = await this._accountService.getSecurityQuestions()
  }

  navigateToUpdateAccount(){
    this.router.navigate(['update_account'])
  }

  changeDisabled() {
    if(this.isDisabled) {
      this.isDisabled = false;
      this.btnText = "Save"
      this.fakePassword = this.account.password;
    }
    else {
      this.isDisabled = true;
      this.btnText = "Edit"
      this.account.password = this.fakePassword;
      this.fakePassword = "*********";
    }
  }
}
<div class="parent">
  <mat-card class="profile-card">
    <mat-card-header>
      <h1>Account Settings</h1>
      <button id="editButton" mat-raised-button color="primary" (click)="changeDisabled()">{{btnText}}</button>
    </mat-card-header>

    <div class="profile-info-section">

      <div class="info-block">
          <h3>Name</h3>
          <mat-form-field>
            <input type="email" matInput [(ngModel)]="account.name" [disabled]="isDisabled">
          </mat-form-field>
      </div>

      <div class="info-block">
          <h3>E-Mail</h3>
          <mat-form-field>
            <input type="email" matInput [(ngModel)]="account.email" [disabled]="isDisabled">
          </mat-form-field>
      </div>

      <div class="info-block">
          <h3>Password</h3>
          <mat-form-field>
            <input type="email" matInput [(ngModel)]="fakePassword" [disabled]="isDisabled">
          </mat-form-field>
      </div>

    </div>
  </mat-card>
</div>
