import { COMMA, ENTER } from '@angular/cdk/keycodes';
import { HttpEventType, HttpParams, HttpResponse } from '@angular/common/http';
import { Component, ViewChild, Inject, Output, EventEmitter } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { MatChipInputEvent } from '@angular/material/chips';
import { MatDialogRef, MAT_DIALOG_DATA } from '@angular/material/dialog';
import { MatTable } from '@angular/material/table';
import { ICategory } from 'src/app/models/category';
import { Recipe } from 'src/app/models/recipe';
import { MediaService } from 'src/app/services/media.service';
import { RecipeService } from 'src/app/services/recipe.service';

@Component({
  selector: 'app-edit-recipe',
  templateUrl: './edit-recipe.component.html',
  styleUrls: ['./edit-recipe.component.css']
})
export class EditRecipeComponent {

  @Output() public restartAfterEdit = new EventEmitter();

  recipe: Recipe;

  ingredientName: string;
  ingredientNames: string[];
  ingredientAmount: string;
  ingredientAmounts: string[];
  ingredients: Ingredient[];

  tags: string;

  selectedCategory: ICategory = {"enumValue": "", "text":""};
  categories: ICategory[];

  selectedTags: string[];

  displayedColumns: string[] = ['ingredientName', 'ingredientAmount', 'actions'];
  tagsAsString: string;
  splitTagsArr:string[];

  visible = true;
  selectable = true;
  removable = true;
  addOnBlur = true;
  readonly separatorKeysCodes: number[] = [ENTER, COMMA];

  selectedFiles: FileList;
  currentFileUpload: File;
  progress: { percentage: number } = { percentage: 0 };
  selectedFile = null;
  mediaString: string = "";

  fileIsSelected = false;


  @ViewChild(MatTable) table: MatTable<Ingredient>;
  ingredient: Ingredient = {
    "ingredientName": '',
    "ingredientAmount": ''
  }

  constructor(public dialogRef:MatDialogRef<EditRecipeComponent>,
    @Inject(MAT_DIALOG_DATA) public data:any,
    private _formBuilder: FormBuilder,
    private _recipeService:RecipeService,
    private _mediaService: MediaService){

      this.ingredientNames = []
      this.ingredientAmounts = []
      this.ingredients = [],
      this.selectedTags =[]
 }

  async ngOnInit(){
    this.recipe= this.data.selectedRecipe;

    this.categories = await this._recipeService.getCategories();
    this.selectedCategory = this.getSelectedCategory(this.recipe.category)

    this.ingredientNames = this.recipe.ingredientNames
    this.ingredientAmounts = this.recipe.ingredientMeasures

    this.ingredients = this.getIgredients(this.ingredientNames, this.ingredientAmounts)
  }

  onCloseClick() {
    this.dialogRef.close();
  }

  onCancel(){
    this.dialogRef.close();
  }

  async updateRecipe(){

    this.selectedCategory = this.setSelectedCategory(this.selectedCategory)
    this.recipe.category = this.selectedCategory.enumValue

    this.recipe.ingredientNames = this.ingredientNames;
    this.recipe.ingredientMeasures = this.ingredientAmounts;

    this.recipe = await this._recipeService.updateRecipe(this.recipe);
    this.dialogRef.close();
 }

 onSubmit(){
  if(this.fileIsSelected) {
    this.progress.percentage = 0;
    this.currentFileUpload = this.selectedFiles.item(0)!;
    this._mediaService.uploadFile(this.currentFileUpload).subscribe(event => {
      if (event.type === HttpEventType.UploadProgress) {
        this.progress.percentage = Math.round(100 * event.loaded / event.total!);
      } else if (event instanceof HttpResponse) {
        this.mediaString = JSON.stringify(event.body)
        this.mediaString = this.mediaString.slice(1, this.mediaString.length - 1)

        // a bit ugly but i cannot get the mediaString out of the subscribe :/
        this.recipe.image = this.mediaString;
        this.updateRecipe();
      }
      this.selectedFiles = undefined!;
      }
    );
  }
  else {
    this.updateRecipe()
  }

}

selectFile(event) {
  this.fileIsSelected = true;
  this.selectedFiles = event.target.files;
}

deleteImage() {
  this._mediaService.deleteFile(this.recipe.image)
  this.recipe.image = "";
}



  /*  --------------------------------------------------------------------------------
        INGREDIENT METHODS
      --------------------------------------------------------------------------------
  */

  getIgredients(ingredientNames: string[], ingredientAmounts: string[]): Ingredient[] {

    let returnIngr: Ingredient[] = [];

    ingredientNames.forEach(function (value, index) {
      let temp: Ingredient = {
        ingredientName: value,
        ingredientAmount: ingredientAmounts.at(index)!
      }
      returnIngr.push(temp)
    });

    return returnIngr;
  }

  onAddIngredient(){
    var name = this.ingredientName
    var amount = this.ingredientAmount

    this.ingredientNames.push(name)
    this.ingredientAmounts.push(amount)

    let ingredient: Ingredient = {
      ingredientName: name,
      ingredientAmount: amount
    }

    this.ingredients.push(ingredient)
    this.table.renderRows();
  }

  onDeleteIngredient(element: Ingredient){

    let deleteIndex = -1

    this.ingredients.forEach(function (value, index) {
      if(value.ingredientName === element.ingredientName && value.ingredientAmount === element.ingredientAmount) {
        deleteIndex = index
      }
    });

    this.ingredientNames.splice(deleteIndex, 1)
    this.ingredientAmounts.splice(deleteIndex, 1)
    this.ingredients.splice(deleteIndex, 1)
    this.table.renderRows();

  }



  /*  --------------------------------------------------------------------------------
        CATEGORY METHODS
      --------------------------------------------------------------------------------
  */

  setSelectedCategory(selectedCategory: ICategory): ICategory{

    for (let category of this.categories){
      if (selectedCategory.text == category.text){
        selectedCategory.enumValue = category.enumValue;
      }
    }
    return selectedCategory;
  }

  getSelectedCategory(categoryEnum: string): ICategory{

    for (let category of this.categories){
      if (categoryEnum == category.enumValue){
        const returnCategory: ICategory = {
          text: category.text,
          enumValue: category.enumValue
        }
        return returnCategory;
      }
    }
    throw Error;
  }



  /*  --------------------------------------------------------------------------------
        TAG METHODS
      --------------------------------------------------------------------------------
  */
  addTag(event: MatChipInputEvent): void {
    const value = (event.value || '').trim();

    if (value) {
      this.recipe.tags.push(value);
    }

    event.chipInput!.clear();
    console.log(this.recipe.tags)
  }

  removeTag(tag: string): void {
    const index = this.recipe.tags.indexOf(tag);

    if (index >= 0) {
      this.recipe.tags.splice(index, 1);
    }
    console.log(this.recipe.tags)
  }
}

export interface Ingredient {
  ingredientName: string;
  ingredientAmount: string;
}

export interface tableColumns
{
    ingredientName: string,
    ingredientAmount: string;
    actions: string
}
<div id="dialog-header">
  <h3 mat-dialog-title id="recipeName">Edit Recipe</h3>

  <mat-dialog-actions id="actions">
    <button mat-mini-fab mat-dialog-close id="close-button" (click)="onCloseClick()">
      <mat-icon>close</mat-icon>
    </button>
  </mat-dialog-actions>
</div>

<mat-dialog-content>

<div id="recipe">

    <div class="recipeField">
      <h3>Edit name</h3>
    <mat-form-field>
      <mat-label>Name</mat-label>
      <input matInput [(ngModel)]="recipe.name" required>
    </mat-form-field>
    </div>

    <div class="recipeField">
      <div *ngIf='this.recipe.image != ""'>
        <h2>Change Image</h2>

        <div class="image-container">
          <div class="content">
            <img [src]="recipe.image" height="300px" width="300px">
          </div>
          <span class="overlay only-show-on-hover">
            <button mat-raised-button (click)="deleteImage()">Delete</button>
          </span>
        </div>

      </div>
      <div *ngIf='this.recipe.image === ""'>
        <h2>Add Image</h2>
      </div>
      <input type="file" id="customFile" (change)="selectFile($event)">

    </div>


    <div class="recipeField">
      <div id="addIngredient">

        <h3>Add Ingredients</h3>

        <mat-form-field>
          <mat-label>Ingredient</mat-label>
          <input matInput [(ngModel)]="ingredientName">
        </mat-form-field>

        <mat-form-field>
          <mat-label>Amount</mat-label>
          <input matInput [(ngModel)]="ingredientAmount">
        </mat-form-field>

        <button mat-raised-button (click)="onAddIngredient()" class="addButton">Add</button>

      </div>

      <table mat-table [dataSource]="ingredients" class="mat-elevation-z8" id="table" >

        <!-- Name Column -->
        <ng-container matColumnDef="ingredientName">
          <th mat-header-cell *matHeaderCellDef>Ingredient</th>
          <td mat-cell *matCellDef="let element"> {{element.ingredientName}} </td>
        </ng-container>

        <!-- Position Column -->
        <ng-container matColumnDef="ingredientAmount">
          <th mat-header-cell *matHeaderCellDef>Amount</th>
          <td mat-cell *matCellDef="let element"> {{element.ingredientAmount}} </td>
        </ng-container>

        <ng-container matColumnDef="actions">
          <th mat-header-cell *matHeaderCellDef> Action </th>
          <td mat-cell *matCellDef="let element"><button mat-raised-button color="primary" (click)="onDeleteIngredient(element)">Delete</button> </td>
        </ng-container>

        <tr mat-header-row *matHeaderRowDef="displayedColumns"></tr>
        <tr mat-row *matRowDef="let row; columns: displayedColumns;"></tr>

      </table>
    </div>


    <div class="recipeField">
      <h3 id="categoryTitle">Category</h3>
      <mat-form-field appearance="fill">
        <mat-label>Category</mat-label>
        <mat-select  [(value)]="selectedCategory.text">
          <mat-option>All</mat-option>
          <mat-option *ngFor="let category of categories" [value]="category.text" >
            {{category.text}}
          </mat-option>
        </mat-select>
      </mat-form-field>
    </div>


    <div class="recipeField">
      <h3>Tags</h3>
    <mat-form-field class="example-chip-list">
      <mat-chip-grid #chipGrid>
        <mat-chip-row *ngFor="let tag of recipe.tags" (removed)="removeTag(tag)">
          {{tag}}
          <button matChipRemove [attr.aria-label]="'remove ' + tag">
            <mat-icon>cancel</mat-icon>
          </button>
        </mat-chip-row>
        <input placeholder="Add tag"
              [matChipInputFor]="chipGrid"
              [matChipInputSeparatorKeyCodes]="separatorKeysCodes"
              [matChipInputAddOnBlur]="addOnBlur"
              (matChipInputTokenEnd)="addTag($event)" />
      </mat-chip-grid>
    </mat-form-field>
    </div>


    <div class="recipeField">
      <h3>Instructions</h3>
    <mat-form-field appearance="fill" class="instructions">
      <mat-label>Instructions</mat-label>
      <textarea matInput  [(ngModel)]="recipe.instructions"></textarea>
    </mat-form-field>
    </div>


</div>


<div id="action-buttons">
  <button mat-raised-button (click)="onCancel()">Cancel</button>
  <button mat mat-raised-button id="create-btn" (click)="onSubmit()">Edit</button>
</div>



</mat-dialog-content>
