import { HttpClient, HttpEvent, HttpParams, HttpRequest } from '@angular/common/http';
import { Injectable } from '@angular/core';
import { firstValueFrom, Observable } from 'rxjs';

@Injectable({
  providedIn: 'root'
})
export class MediaService {

  private _baseUrl = "http://localhost:8085/media";


  constructor(private http: HttpClient) { }

  async uploadFile(file: File): Promise<any> {
    //let queryParams = new HttpParams();
    //queryParams = queryParams.append("file", file);

    const formData = new FormData();
    formData.append("file", file);
    return firstValueFrom(this.http.post<any>(`${this._baseUrl}/upload`, formData));

  }

  pushFileToStorage(file: File): Observable<HttpEvent<{}>> {
    const data: FormData = new FormData();
    data.append('file', file);
    const newRequest = new HttpRequest('POST', 'http://localhost:8085/media/upload', data, {
    reportProgress: true,
    responseType: 'text'
    });
    return this.http.request(newRequest);
  }

}
