<div id="fridge">

  <div id="top-part">
    <h1>Fridge items</h1>
    <button mat-raised-button (click)="openAddDialog()">
      <mat-icon matListItemIcon>add</mat-icon>
      Add an item
    </button>
  </div>

  <div *ngIf="makeNoItemsElementVisible == true">
    <h3 class="noItemsElement">You don't have any fridge items yet.</h3>
  </div>

  <mat-list>

    <mat-list-item *ngFor="let fridgeItem of fridgeItems">

        <div class="item">

          <img src="../../../assets/groceries-paper-bag.jpg" alt="Fridge item photo">

          <div class="item-text">
            <h3 class="item-name">{{fridgeItem.name}}</h3>
            <div class="item-amount">Amount: {{fridgeItem.amount}}</div>
            <div class="item-date">Expiration date: {{handleDate(fridgeItem.expirationDate)}}</div>
          </div>

          <button mat-icon-button id="delete-button" (click)="openEditDialog(fridgeItem)">
            <mat-icon id="delete-icon">edit</mat-icon>
          </button>
          <button mat-icon-button id="delete-button" (click)="deleteItem(fridgeItem)">
            <mat-icon id="delete-icon">delete</mat-icon>
          </button>

        </div>

    </mat-list-item>

  </mat-list>


</div>

<div id="dialog">

  <h1 mat-dialog-title>Add an item</h1>

  <div mat-dialog-content>

    <form class="item-form">
      <mat-form-field>
        <mat-label>Name</mat-label>
        <input matInput placeholder="Ex. Tomato" name="name" [(ngModel)]="newItem.name">
      </mat-form-field>

      <mat-form-field>
        <mat-label>Amount</mat-label>
        <input matInput placeholder="Ex. 5" name="amount" [(ngModel)]="newItem.amount">
      </mat-form-field>

      <mat-form-field appearance="fill">
        <mat-label>Expiration date</mat-label>
        <input matInput [matDatepicker]="picker" name="expirationDate" [(ngModel)]="newItem.expirationDate">
        <mat-hint>MM/DD/YYYY</mat-hint>
        <mat-datepicker-toggle matIconSuffix [for]="picker"></mat-datepicker-toggle>
        <mat-datepicker #picker></mat-datepicker>
      </mat-form-field>
    </form>

  </div>

  <div mat-dialog-actions>
    <button mat-raised-button (click)="onCancelClick()">Cancel</button>
    <button mat-raised-button (click)="onAddClick()">Add</button>
  </div>



</div>




