import { Component, OnInit, Inject } from '@angular/core';
import {MatDialog, MAT_DIALOG_DATA, MatDialogRef} from '@angular/material/dialog';
import { Recipe } from 'src/app/models/recipe';
import { RecipeService } from 'src/app/services/recipe.service';

@Component({
  selector: 'app-recipe-details',
  templateUrl: './recipe-details.component.html',
  styleUrls: ['./recipe-details.component.css']
})
export class RecipeDetailsComponent implements OnInit {
  selectedRecipe: Recipe;

  constructor(
    public dialogRef: MatDialogRef<RecipeDetailsComponent>,
    @Inject(MAT_DIALOG_DATA) public data: DialogData) {
    }

  onNoClick(): void {
    this.dialogRef.close();
  }

  public icon = 'favorite_border'
  markFavourite(newIcon: string){
    this.icon = newIcon;
  }

  ngOnInit() {
    this.selectedRecipe = this.data.selectedRecipe;
    console.log(this.selectedRecipe)
  }

  onCloseClick() {
    this.dialogRef.close();
  }
}

export interface DialogData {
  selectedRecipe: Recipe
}
<div id="dialog-header">
  <h3 mat-dialog-title id="recipeName">{{selectedRecipe.name}}</h3>

  <mat-dialog-actions id="actions">
    <button mat-mini-fab mat-dialog-close id="close-button">
      <mat-icon>close</mat-icon>
    </button>
  </mat-dialog-actions>
</div>


<mat-dialog-content class="mat-typography">

  <img class="centeredImage" [src]="selectedRecipe.image" height="300px" width="300px">

  <button mat-button class="tag-button" *ngFor="let tags of selectedRecipe.tags">{{tags}}</button>

  <h4 class="headers" id="ingredientsHeader"> Here's what you need!</h4>
    <ul style="list-style-type:circle;">
      <li *ngFor="let measures of selectedRecipe.ingredientMeasures">{{measures}}</li>
      <li *ngFor="let ingredient of selectedRecipe.ingredientNames">{{ingredient}}</li>
    </ul>

  <h4 class="headers">How to make it: </h4>
    <p id="recipe-description">{{selectedRecipe.instructions}}</p>
</mat-dialog-content>
