import { HttpClient, HttpParams } from '@angular/common/http';
import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormGroup } from '@angular/forms';
import { Router } from '@angular/router';
import { Account } from 'src/app/models/account';
import { AccountService } from 'src/app/services/account.service';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css']
})
export class LoginComponent implements OnInit {

  public loginForm!: FormGroup;
  public queryParams = new HttpParams();
  public account: Account = {
    "id": "",
    "name": "",
    "email": "",
    "password": "",
    "securityQuestion": "",
    "securityAnswer": ""
  }

  constructor(private _accountService: AccountService,
    private formBuilder: FormBuilder,
    private http:HttpClient,
    private router:Router ) { }

  ngOnInit(): void {
    this.loginForm = this.formBuilder.group({
      email:[''],
      password:[''],
    })
  }

  async login(){
    this.queryParams = this.queryParams.append("email", this.account.email);
    this.queryParams = this.queryParams.append("password", this.account.password);
    this.account = await this._accountService.findAccount(this.queryParams);

    console.log(this.account)

    if(this.account.id == ""){
      alert("User not found. Please check again your email and password.")
    }
    else {
      this.loginForm.reset();
      this._accountService.sendOwnerAccountToServices(this.account.id);
      this.router.navigate(['home']);
    }


    /*this.http.get<any>("http://localhost:8085/api/v1/accounts")
    .subscribe(res =>{
      let user = res.find((a:Account)=>{
        return a.email === this.loginForm.value.email && a.password === this.loginForm.value.password
      }
      )
      console.log(res)
      if(user){
        alert("Login success")
        console.log(user.id);
        this._accountService.sendOwnerAccountToServices(user.id);
        this.router.navigate(['home']);
      } else{
        alert("User not found")
      }
    },
    err=>{
      alert("Something went wrong !!")

    })*/
  }


  }


<div class="bg">
    <div class="container">
        <div class="row">
            <div class="col-md-6">
                <div class="card">
                    <div class="text-center">
                        <h1>Welcome back.</h1>
                        <h6>Clean out your fridge and enjoy a delicious meal</h6>
                    </div>
                    <form class="sign-up-form" [formGroup]="loginForm">
                        <div class="mb-3">
                            <label for="exampleInputEmail1" class="form-label">Email address</label>
                            <input type="email" class="form-control" id="exampleInputEmail1" aria-describedby="emailHelp" 
                            formControlName="email" 
                            [(ngModel)]="account.email"
                            [ngClass]="{ 'is-invalid': submitted && f['email'].errors }"
                            required>
                            <div *ngIf="submitted && f['email'].errors" class="invalid-feedback">
                                <div *ngIf="f['email'].errors['required']">Email is required</div>
                                <div *ngIf="f['email'].errors['email']">Email is invalid</div>
                              </div>
                            <div id="emailHelp" class="form-text">We'll never share your email with anyone else.</div>
                          </div>

                          <div class="mb-3">
                            <label for="exampleInputPassword1" class="form-label">Password</label>
                            <input type="password" class="form-control" id="exampleInputPassword1" 
                            formControlName="password" 
                            [(ngModel)]="account.password"
                            [ngClass]="{ 'is-invalid': submitted && f['password'].errors }"
                            required>
                            <div *ngIf="submitted && f['password'].errors" class="invalid-feedback">
                                <div *ngIf="f['password'].errors['required']">Password is required</div>
                            </div>
                          </div>
                          <div>
                            <button type="button" class="btn btn-primary" (click)="login()">Login</button>
                            <a style="display: flex; justify-content: flex-end" routerLink="../sign-up"> New User? Click to sign up! </a>
                          </div>
                    </form>
                </div>
            </div>
        </div>
    </div>
    </div>
    <router-outlet></router-outlet>
