import { Component } from '@angular/core';
import { HttpParams } from '@angular/common/http';
import { RecipeService } from 'src/app/services/recipe.service';
import { IRecipe } from 'src/app/models/recipe';

@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.css']
})
export class HomeComponent {
  public recipes = [] as any;
  public errorMsg = "" as any;
  public queryParams = new HttpParams();

  constructor(private _recipeService: RecipeService) {
  }

  ngOnInit(): void {
    this._recipeService.getRecipes(this.queryParams)
        .subscribe(data => this.recipes = data
          .sort((a: IRecipe, b: IRecipe) => a.name.localeCompare(b.name)),
                  error => this.errorMsg = error);
  }

  restart(queryParams: HttpParams): void {
    this._recipeService.getRecipes(queryParams)
        .subscribe(data => this.recipes = data
          .sort((a: IRecipe, b: IRecipe) => a.name.localeCompare(b.name)));
  }

  sort(criteria: string): void {
    if(criteria == "nameA"){
      this.recipes = this.recipes.sort((a: IRecipe, b: IRecipe) => a.name.localeCompare(b.name));
    }
    if(criteria == "nameZ"){
      this.recipes = this.recipes.sort((a: IRecipe, b: IRecipe) => a.name.localeCompare(b.name));
      this.recipes = this.recipes.reverse();
    }
  }
}
<div class="home">
  <div>
    <img class="logo" src="../assets/logo-big-black.PNG" alt="Logo">
  </div>
  <app-recipe-sort-filter (newQueryEvent)="restart($event)" (newSortEvent)="sort($event)"></app-recipe-sort-filter>
  <app-recipe-grid class="recipes" [recipes]="recipes"></app-recipe-grid>
</div>
