import { HttpClient } from '@angular/common/http';
import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormGroup } from '@angular/forms';
import { Router } from '@angular/router';
import { AccountService } from 'src/app/services/account.service';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css']
})
export class LoginComponent implements OnInit {

  public loginForm!: FormGroup;
  constructor(private accountService: AccountService, private formBuilder: FormBuilder, private http:HttpClient, private router:Router ) { }

  ngOnInit(): void {
    this.loginForm = this.formBuilder.group({
      email:[''],
      password:[''],
    })
  }
 

  /*onLogin(){
      this.http.get<any>("http://localhost:8085/api/v1/accounts")
      .subscribe(res=>{
        const user = res.find((a:any)=>{
          return a.email === this.loginForm.value.email && a.password === this.loginForm.value.password 
        });
        if(user){
          alert('Login Succesful');
          this.loginForm.reset()
        this.router.navigate(["home"])
        }else{
          alert("user not found")
        }
      },err=>{
        alert("Something went wrong")
      })
  }*/

  login(){
    this.http.get<any>("http://localhost:8085/api/v1/accounts")
    .subscribe(res =>{
      const user = res.find((a:any)=>{
        return a.email === this.loginForm.value.email && a.password === this.loginForm.value.password
      }
      )
      console.log(res)
      if(user){
      alert("Login success")
      this.loginForm.reset();
      this.router.navigate(['home']);
      } else{
        alert("User not found")
      }
    },
    err=>{
      alert("Something went wrong !!")
    
    })
  }
  }


<div class="bg">
    <div class="container">
        <div class="row">
            <div class="col-md-6">
                <div class="card">
                    <div class="text-center">
                        <h1>Welcome back.</h1>
                        <h6>Clean out your fridge and enjoy a delicious meal</h6>
                    </div>
                    <form class="sign-up-form">
                        <div class="mb-3">
                            <label for="exampleInputEmail1" class="form-label">Email address</label>
                            <input type="email" class="form-control" id="exampleInputEmail1" aria-describedby="emailHelp" name="email" [(ngModel)]="account.email">
                            <div id="emailHelp" class="form-text">We'll never share your email with anyone else.</div>
                          </div>
                          <div class="mb-3">
                            <label for="exampleInputPassword1" class="form-label">Password</label>
                            <input type="password" class="form-control" id="exampleInputPassword1" name="password" [(ngModel)]="account.password">
                          </div>
                          <div>
                            <button type="button" class="btn btn-primary" (click)="login()">Login</button>
                            <a style="display: flex; justify-content: flex-end" routerLink="../sign-up"> New User? Click to sign up! </a>
                          </div>
                    </form>
                </div>
            </div>
        </div>
    </div>
    </div>
    <router-outlet></router-outlet>
