import { Component, EventEmitter, Inject, Output, ViewChild } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { MatDialogRef, MAT_DIALOG_DATA } from '@angular/material/dialog';
import { Recipe } from 'src/app/models/recipe';
import { RecipeService } from 'src/app/services/recipe.service';
import { DialogData, RecipeDetailsComponent } from 'src/app/shared/components/recipe-details/recipe-details.component';
import {MatTable} from '@angular/material/table';
import { ICategory } from 'src/app/models/category';
import { MatChipInputEvent } from '@angular/material/chips';
import { COMMA, ENTER } from '@angular/cdk/keycodes';
import { HttpParams } from '@angular/common/http';


@Component({
  selector: 'app-create-recipe',
  templateUrl: './create-recipe.component.html',
  styleUrls: ['./create-recipe.component.css']
})
export class CreateRecipeComponent {
  recipeForm: FormGroup;

  recipe: Recipe;

  ingredienNames: string[];
  ingredientAmounts: string[];
  ingredients: Ingredient[];

  selectedCategory: ICategory = {"enumValue": "", "text":""};
  categories: ICategory[];

  selectedTags: string[];

  displayedColumns: string[] = ['ingredientName', 'ingredientAmount'];

  public queryParams = new HttpParams();
  @Output() public newQueryEvent = new EventEmitter<HttpParams>();

  @ViewChild(MatTable) table: MatTable<Ingredient>;

  constructor(public dialogRef:MatDialogRef<CreateRecipeComponent>,
    @Inject(MAT_DIALOG_DATA) public data:any,
    private _formBuilder: FormBuilder,
    private _recipeService:RecipeService){

      this.ingredienNames = []
      this.ingredientAmounts = []
      this.ingredients = [],
      this.selectedTags =[]
 }

  async ngOnInit(){
    this.recipeForm=this._formBuilder.group({
      ingredientName:[null, Validators.required],
      ingredientAmount:[null, Validators.required],
      name:[null, Validators.required],
      category:[null, Validators.required],
      tags:[null, Validators.required],
      instructions:[null, Validators.required]
    })

    this.categories = await this._recipeService.getCategories();
  }

  onCloseClick() {
    this.dialogRef.close();
  }

  async onSubmit(){

    this.selectedCategory = this.setSelectedCategory(this.selectedCategory)

    this.recipe={
      id: '',
      name:this.recipeForm.get('name')?.value,
      ingredientNames: this.ingredienNames,
      ingredientMeasures: this.ingredientAmounts,
      category: this.selectedCategory.enumValue,
      tags: this.selectedTags,
      instructions:this.recipeForm.get('instructions')?.value,
      image:'http://localhost:8085/media/download/63c95e3d664c9260ee663f9c',
      link:'',
      ownerAccount:''
    }
    //this.recipe = await this._recipeService.createRecipe(this.recipe);
    console.log(this.recipe)
    this.dialogRef.close();
 }


  async onAddIngredient(){
    var name = this.recipeForm.get("ingredientName")?.value
    var amount = this.recipeForm.get("ingredientAmount")?.value

    this.ingredienNames.push(name)
    this.ingredientAmounts.push(amount)

    const ingredient: Ingredient = {
      ingredientName: name,
      ingredientAmount: amount
    }

    this.ingredients.push(ingredient)
    this.table.renderRows();

  }

  setSelectedCategory(selectedCategory: ICategory): ICategory{

    for (let category of this.categories){
      if (selectedCategory.text == category.text){
        selectedCategory.enumValue = category.enumValue;
      }
    }
    return selectedCategory;
  }

  visible = true;
  selectable = true;
  removable = true;
  addOnBlur = true;
  readonly separatorKeysCodes: number[] = [ENTER, COMMA];

  addTag(event: MatChipInputEvent): void {
    const value = (event.value || '').trim();

    if (value) {
      this.selectedTags.push(value);
    }

    event.chipInput!.clear();
  }

  removeTag(tag: string): void {
    const index = this.selectedTags.indexOf(tag);

    if (index >= 0) {
      this.selectedTags.splice(index, 1);
    }
  }
}

export interface Ingredient {
  ingredientName: string;
  ingredientAmount: number;
}
<div id="dialog-header">
    <h3 mat-dialog-title id="recipeName">Create Custom Recipe</h3>

    <mat-dialog-actions id="actions">
      <button mat-mini-fab mat-dialog-close id="close-button" (click)="onCloseClick()">
        <mat-icon>close</mat-icon>
      </button>
    </mat-dialog-actions>
</div>

<mat-dialog-content>

  <div id="ingredients">

    <form [formGroup]="recipeForm">

      <h3>Name your recipe</h3>
      <mat-form-field>
        <mat-label>Name</mat-label>
        <input matInput formControlName="name" required>
      </mat-form-field>

      <div class="add">

        <h3>Add Ingredients</h3>

        <mat-form-field>
          <mat-label>Ingredient</mat-label>
          <input matInput formControlName="ingredientName" required>
        </mat-form-field>

        <mat-form-field>
          <mat-label>Amount</mat-label>
          <input matInput id="hi" formControlName="ingredientAmount" required>
        </mat-form-field>

        <button mat-raised-button (click)="onAddIngredient()" class="addButton">Add</button>

      </div>

      <table mat-table [dataSource]="ingredients" class="mat-elevation-z8" id="table" >

        <!-- Name Column -->
        <ng-container matColumnDef="ingredientName">
          <th mat-header-cell *matHeaderCellDef>Ingredient</th>
          <td mat-cell *matCellDef="let element"> {{element.ingredientName}} </td>
        </ng-container>

        <!-- Position Column -->
        <ng-container matColumnDef="ingredientAmount">
          <th mat-header-cell *matHeaderCellDef>Amount</th>
          <td mat-cell *matCellDef="let element"> {{element.ingredientAmount}} </td>
        </ng-container>

        <tr mat-header-row *matHeaderRowDef="displayedColumns"></tr>
        <tr mat-row *matRowDef="let row; columns: displayedColumns;"></tr>

      </table>

      <div class="spacer"></div>

        <h3>Category</h3>
        <mat-form-field appearance="fill">
          <mat-label>Category</mat-label>
          <mat-select formControlName="category" [(value)]="selectedCategory.text">
            <mat-option>All</mat-option>
            <mat-option *ngFor="let category of categories" [value]="category.text">
              {{category.text}}
            </mat-option>
          </mat-select>
        </mat-form-field>

      <div class="spacer"></div>

        <h3>Tags</h3>
        <mat-form-field class="example-chip-list">
          <mat-chip-grid #chipGrid>
            <mat-chip-row *ngFor="let tag of selectedTags" (removed)="removeTag(tag)">
              {{tag}}
              <button matChipRemove [attr.aria-label]="'remove ' + tag">
                <mat-icon>cancel</mat-icon>
              </button>
            </mat-chip-row>
            <input placeholder="Add tag"
                  [matChipInputFor]="chipGrid"
                  [matChipInputSeparatorKeyCodes]="separatorKeysCodes"
                  [matChipInputAddOnBlur]="addOnBlur"
                  (matChipInputTokenEnd)="addTag($event)"
                  formControlName="tags"/>
          </mat-chip-grid>
        </mat-form-field>

        <div class="spacer"></div>

        <h3>Instructions</h3>
        <mat-form-field appearance="fill" class="instructions">
          <mat-label>Instructions</mat-label>
          <textarea matInput formControlName="instructions"></textarea>
        </mat-form-field>

    </form>
  </div>


  <div id="action-buttons">
    <button mat-raised-button>Cancel</button>
    <button mat mat-raised-button id="create-btn" (click)="onSubmit()">Create</button>
  </div>



</mat-dialog-content>
