import { Component, OnInit } from '@angular/core';
import { ActivatedRoute, ParamMap, Router } from '@angular/router';
import { interval } from 'rxjs';
import { FridgeItem } from 'src/app/models/fridgeItem';
import { FridgeService, NotifItem } from 'src/app/services/fridge.service';

@Component({
  selector: 'app-header',
  templateUrl: './header.component.html',
  styleUrls: ['./header.component.css']
})
export class HeaderComponent implements OnInit {

  public expFrigdeItems: NotifItem[];
  public count: number;

  constructor(private _fridgeService: FridgeService,
    private route: ActivatedRoute, private router: Router) {

      interval(5000).subscribe(x => {
        this.getNotif();
      })
    }

  async ngOnInit() {
    this.getNotif()
  }

  async getNotif() {
    this.expFrigdeItems = await this._fridgeService.getUpdatedNotifications();
    this.count = this.expFrigdeItems.length;
  }
}
<mat-toolbar color="teal">
  <span><mat-icon>kitchen</mat-icon></span>
  <div>
    <img class="logo" src="../assets/logo-small-white.PNG" alt="Logo">
  </div>
  <div class="menu">
  <a mat-raised-button color="accent" routerLink="home">Home</a>
  <a mat-button routerLink="/fridge">Fridge</a>
  <a mat-button routerLink="/my_recipes">My recipes</a>
  </div>
  <div class="spacer"></div>

  <button mat-button [matMenuTriggerFor]="notif">
    <mat-icon [matBadge]="count">notifications</mat-icon>
    <mat-menu #notif="matMenu">
      <button mat-menu-item *ngFor="let fridgeItem of expFrigdeItems" routerLink="/fridge">{{fridgeItem.item.name}} - {{fridgeItem.status}}</button>
   </mat-menu>
  </button>

  <a mat-button routerLink="/account_settings"><mat-icon>manage_accounts</mat-icon></a>
  <a mat-button routerLink="/login"><mat-icon>logout</mat-icon></a>
</mat-toolbar>
