import { Component, OnInit } from '@angular/core';
import { Account } from 'src/app/models/account';
import {FormBuilder, FormControl, FormGroup, Validators} from '@angular/forms';
import { Router } from '@angular/router';
import { AccountService } from 'src/app/services/account.service';
import { HttpClient } from '@angular/common/http';
import {STEPPER_GLOBAL_OPTIONS} from '@angular/cdk/stepper';
import { ISecurityQuestion } from 'src/app/models/securityQuestions';
import { ValidationService } from 'src/app/services/validation.service';
import { ValidationErrors } from '@angular/forms';
import { MatOptionSelectionChange } from '@angular/material/core';
import { throwError } from 'rxjs';
import { MatSnackBar } from '@angular/material/snack-bar';
import { MatStepper, MatStepperModule } from '@angular/material/stepper';


@Component({
  selector: 'app-sign-up',
  templateUrl: './sign-up.component.html',
  styleUrls: ['./sign-up.component.css'],
  providers: [
    {
      provide: STEPPER_GLOBAL_OPTIONS,
      useValue: {showError: true},
    },
  ],
})
export class SignUpComponent implements OnInit {
  firstFormGroup: FormGroup;
  secondFormGroup: FormGroup;
  signUpSuccessful: boolean;

  account: Account;

  securityQuestions: ISecurityQuestion[]

  visible:boolean = true;
  changetype:boolean =true;
  _stepper:MatStepper;
  isLinear:true;

  constructor( private _formBuilder: FormBuilder,
    private _accountService: AccountService,
    private router: Router,
    private _snackBar:MatSnackBar,
    ) {

  }

  ngOnInit() {

    this.getSecQuestions()

    this.firstFormGroup = this._formBuilder.group({
      username : [null, Validators.required],
      email: [null, [Validators.required, Validators.email]],
      password: [null, Validators.required],
      passwordRepeat: new FormControl("", Validators.required)
    }, {
      validator: this.ConfirmedValidator("password", "passwordRepeat") //works both ways
    });

    this.secondFormGroup = this._formBuilder.group({
      securityQuestion: [null, Validators.required],
      securityAnswer: [null, Validators.required]
    });

  }

  async getSecQuestions() {
    const res: any = await this._accountService.getSecurityQuestions();
      this.securityQuestions = res;
      console.log(this.securityQuestions)
  }

  ConfirmedValidator(controlName: string, matchingControlName: string) {
    return (formGroup: FormGroup) => {
      const control = formGroup.controls[controlName];
      const matchingControl = formGroup.controls[matchingControlName];
      if (
        matchingControl.errors &&
        !matchingControl.errors['confirmedValidator']
      ) {
        return;
      }
      if (control.value !== matchingControl.value) {
        matchingControl.setErrors({ confirmedValidator: true });
      } else {
        matchingControl.setErrors(null);
      }
    };
  }


  async onSignUp(){

    if (this.firstFormGroup.valid && this.secondFormGroup.valid) {
      this.account = {
        id: "",
        name: this.firstFormGroup.get('username')?.value,
        email: this.firstFormGroup.get('email')?.value,
        password: this.firstFormGroup.get('password')?.value,
        securityQuestion: this.secondFormGroup.get('securityQuestion')?.value,
        securityAnswer: this.secondFormGroup.get('securityAnswer')?.value
      }
      console.log(this.account)

      const res: any = await this._accountService.createAccount(this.account);
      this.account = res; //to get the actual id
      console.log(res)

      this.firstFormGroup.reset();  //check if you actually need it
      this.secondFormGroup.reset(); //same as above

      this.router.navigate(['home']);
    }
    else if(this.firstFormGroup.invalid ||this.secondFormGroup.invalid){
      this._snackBar.open('Did you complete all required fields?', 'Oops!', {
        duration: 5000,
      });
      return;
    }
    else {
      (err: any) => {
        console.log(err)
        this.handleError(err)
      }
    }

  }

  viewpass(){
    this.visible = !this.visible;
    this.changetype = !this.changetype;
  }

  handleError(err: { error: any; message: any; status: any; }) {
    let errorMessage = '';
    if (err.error instanceof ErrorEvent) {
      // if error is client-side error
      errorMessage = `Error: ${err.message}`;
    } else {
      // if error is server-side error
      errorMessage = `Error Code: ${err.status}\nMessage: ${err.message}`;
    }
    alert(errorMessage);
    return throwError(errorMessage);
  }
}
<div class="bg">
    <mat-card>

        <mat-card-header>
            <mat-card-title>Create an account</mat-card-title>
        </mat-card-header>

        <mat-card-content>
            <form class="sign-up-form" (ngSubmit)="onSignUp()" >
                <mat-stepper id="stepper" #stepper="matHorizontalStepper" [linear]="isLinear">
                    <mat-step [stepControl]="firstFormGroup" >
                        <form [formGroup]="firstFormGroup">
                            <ng-template matStepLabel class="stepper-label"><div class="stepper-label">Create account</div></ng-template>

                            <mat-form-field>
                                <mat-label>Username</mat-label>
                                <input matInput formControlName="username" required>
                            </mat-form-field>

                            <mat-form-field>
                                <mat-label>Email</mat-label>
                                <input matInput formControlName="email" required>
                            </mat-form-field>

                            <mat-form-field>
                                <mat-label>Password</mat-label>
                                <input matInput formControlName="password" required [type]="changetype?'password':'text'">
                                <span class="eyeicon"><mat-icon (click)="viewpass()" [ngClass]="visible?'visibility':'visibility_off'">
                                    visibility
                                </mat-icon></span>
                            </mat-form-field>

                            <mat-form-field>
                                <mat-label for="passwordRepeat">Confirm password</mat-label>
                                <input matInput formControlName="passwordRepeat" required [type]="changetype?'password':'text'" >
                                <!--<div class="invalid-feedback">Passwords don't match</div>-->
                                <span class="eyeicon"><mat-icon (click)="viewpass()" [ngClass]="visible?'visibility':'visibility_off'">
                                    visibility
                                </mat-icon></span>
                            </mat-form-field>
                            <div class="alert"
                                        *ngIf="firstFormGroup.errors?.['confirmedValidator']
                                        || (firstFormGroup.controls['passwordRepeat'].hasError('required') && firstFormGroup.controls['passwordRepeat'].touched)">
                                Passwords don't match
                            </div> <!-- password matching works but somehow div doesn't show up-->


                            <div class="buttons">
                                <button mat-raised-button matStepperNext matStepperNext>Next</button>
                            </div>
                        </form>
                    </mat-step>

                    <mat-step [stepControl]="secondFormGroup" [editable]="false" [completed]="false"  (click)="$event.stopPropagation()" >
                        <form [formGroup]="secondFormGroup">
                            <ng-template matStepLabel><div class="stepper-label">Security question</div></ng-template>

                                <mat-form-field>
                                    <mat-label>Select a security question</mat-label>
                                    <mat-select formControlName="securityQuestion" required>
                                        <mat-option *ngFor="let question of securityQuestions" value="question.enumValue">
                                            {{question.text}}
                                        </mat-option>
                                    </mat-select>
                                </mat-form-field>

                                <mat-form-field>
                                    <mat-label>Type your answer here</mat-label>
                                    <input matInput formControlName="securityAnswer" required>
                                </mat-form-field>

                                <div class="buttons">
                                    <button mat-raised-button matStepperPrevious>Back</button>
                                    <button mat-raised-button (click)="onSignUp()">Sign Up</button>
                                </div>
                        </form>
                    </mat-step>
                </mat-stepper>
                <div>
                    <a style="display: flex; justify-content: flex-end" routerLink="../login"> Already registered? </a>
                  </div>
            </form>
        </mat-card-content>

    </mat-card>
</div>
<router-outlet></router-outlet>
